func:
var links="";
var a="";
var count=global.get("count")+1;
global.set("count", count);

var data = msg.tweet.created_at+","+msg.tweet.user.screen_name+","
+msg.tweet.user.followers_count+","+msg.sentiment.score;
if (msg.payload.includes("http")) {
   links="Links:"+msg.tweet.entities.urls[0].expanded_url;
} 
if (msg.payload.includes("RT @")) {
   a=a+";"+"Retweet";
} 
if (msg.payload.includes("Books")) {
   a=a+";"+"Books";
} 
msg.links=links;
msg.extra="msg: "+count+" : About: "+a;
msg.csvrow=data;
return msg;

