wordsToLookFor = global.get("wordsToLookFor");
msg.wordsToLookFor = wordsToLookFor;
var a="";
var count=global.get("count")+1;
global.set("count", count);

var data = msg.tweet.created_at+","+msg.tweet.user.screen_name+","
+msg.tweet.user.followers_count+","+msg.sentiment.score;
var $text = msg.payload;
// Convert to lowercase
$text = $text.toLowerCase();
var links = $text.match(/\bhttps?:\/\/\S+/gi);
// replace unnesessary chars. leave only chars, numbers and space
$text = $text.replace(/[^\w\d ]/g, '');
var words = $text.split(' ');

// remove $commonWords
keywords1 = words.filter(function (word) {
    return $commonWords.indexOf(word) === -1;
});

keywords = [];
  for (var i = 0; i < keywords1.length; i++) {
    if (keywords.indexOf(keywords1[i])==-1) {
      keywords.push(keywords1[i]);
    }
  }

keywords = [];
for (var j = wordsToLookFor.length - 1; j >= 0; --j) {
    if ($text.indexOf(wordsToLookFor[j]) != -1) {
         // str contains wordsToLookFor[i]
         keywords.push(wordsToLookFor[j]);
    }
}


if (msg.payload.includes("http")) {
   links1=msg.tweet.entities.urls;
  for (i = 0; i < links1.length; i++) {
      links.push(links1[i].url);
      links.push(links1[i].expanded_url);
  }
} 
if (msg.payload.includes("RT @")) {
   a=a+";"+"Retweet";
} 
if (msg.payload.includes("Books")) {
   a=a+";"+"Books";
} 

var output={};
output.msgNumber=count;
var create_dt=new Date(msg.tweet.created_at);
let ye = new Intl.DateTimeFormat('en', { year: 'numeric' }).format(create_dt);
let mo = new Intl.DateTimeFormat('en', { month: 'short' }).format(create_dt);
let da = new Intl.DateTimeFormat('en', { day: '2-digit' }).format(create_dt);
console.log(`${da}-${mo}-${ye}`);
output.timestamp=da+"-"+mo+"-"+ye;
output.messageText=msg.payload;
output.topic=msg.topic;
output.sentimentScore=msg.sentiment.score;
output.links=links;
output.extra="msg: "+count+" : About: "+a;
output.csvrow=data;
output.keywords=keywords;
output.source=msg.tweet.source;
msg.output=output;





return msg;

